#include <stdio.h> int main() {    int T,x,y,s;    scanf("%d",&T);    while(T--)    {        s=0;        scanf("%d%d",&x,&y);        while(y)        {            if(x%2!=0)            {                s += x;                x++;                y--;            }            else            {                x++;            }        }        printf("%d\n",s);    }  return 0;}