#include <stdio.h>int main() {    while(1)    {        int n,i,j=5,s=0;        scanf("%d",&n);        if(n==0)            break;        else        {            while(j>0)            {                if(n%2 == 0)                {                    s += n;                    n++;                    j--;                }                else                {                    n++;                }            }        }        printf("%d\n",s);    }    return 0;}